function shuffleColumns(n){const e=document.getElementById(n),o=Array.from(e.children);for(let n=o.length-1;n>0;n--){const e=Math.floor(Math.random()*(n+1));[o[n],o[e]]=[o[e],o[n]]}e.innerHTML="",o.forEach((n=>e.appendChild(n)))}"/team.html"==window.location.pathname&&(window.addEventListener("load",shuffleColumns("organisers")),window.addEventListener("load",shuffleColumns("volunteers")));