function shuffleColumns(n){const o=document.getElementById(n),e=Array.from(o.children);for(let n=e.length-1;n>0;n--){const o=Math.floor(Math.random()*(n+1));[e[n],e[o]]=[e[o],e[n]]}o.innerHTML="",e.forEach((n=>o.appendChild(n)))}"/team.html"==window.location.pathname&&window.addEventListener("load",shuffleColumns("organisers"));